html:
{%- macro add_header(page_num, max_pages, doc, letter_head, no_letterhead) -%}
    {% if letter_head and not no_letterhead %}
        <div class="letter-head" style="height: 25mm;">
            {{ letter_head }}
        </div>
    {% endif %}

    {% if max_pages > 1 %}
        <p class="text-right">
            {{ _("Page {0} of {1}").format(page_num, max_pages) }}
        </p>
    {% endif %}
{%- endmacro -%}

<!-- Header Section -->
<div id="header-html" class="hidden-pdf" style="margin-top: 100px;">
    {{ add_header(0, 1, doc, letter_head, no_letterhead) if add_header(0, 1, doc, letter_head, no_letterhead) else '' }}
</div>
<!--Print format Body section Start  -->


<!--Print format Body section End  -->

<!-- Footer Section -->
<div id="footer-html" class="visible-pdf">
    <!--{% if not no_letterhead and footer %}-->
    <div class="letter-head-footer" style="height: -15mm;">
        {{ footer if footer else '' }}
    </div>
    <!--{% endif %}-->

    <p class="text-center small page-number visible-pdf" align="center">{{ _("Page {0} of {1}").format('<span class="page"></span>', '<span class="topage"></span>') }}</p>
</div>
